    private void cadastrar(){
        Pessoa p = new Pessoa();
        
        p.setNome(jtNome.getText());
        p.setIdade(Integer.parseInt(jtIdade.getText()));
        p.setAltura(Integer.parseInt(jtAltura.getText()));
        p.setPeso(Double.parseDouble(jtPeso.getText()));
        p.setSexo(bgSexo.getSelection().getActionCommand());
        p.setPercentGordura(Double.parseDouble(jtGordura.getText()));
        
        jtRelatorio.setText(p.toString());
    }
    
    private void limpar(){
        jtNome.setText("");
        jtAltura.setText("");
        jtPeso.setText("");
        jtIdade.setText("");     
        jtRelatorio.setText("");
        bgSexo.clearSelection();
        jtNome.grabFocus();
    }
    
    private void jbLimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbLimparActionPerformed
        limpar();
    }//GEN-LAST:event_jbLimparActionPerformed

    private void jbCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbCadastrarActionPerformed
        cadastrar();
    }//GEN-LAST:event_jbCadastrarActionPerformed

    private void jtNomeKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jtNomeKeyPressed
        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            jtAltura.grabFocus();
        }
    }//GEN-LAST:event_jtNomeKeyPressed

